    private void LimpiarCasillas(){
        txtId.setText(null);
        txtNombre.setText(null);
        txtDireccion.setText(null);
        txtTelefono.setText(null);
    }
    private void btnNuevoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNuevoActionPerformed
        try {
            db.MaxId(conn, ct);
            txtId.setText(String.valueOf(ct.getId()));
        } catch (SQLException ex) {
        // Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_btnNuevoActionPerformed

    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        try {
            ct.setId(Integer.parseInt(txtId.getText()));
            ct.setNombre(txtNombre.getText());
            ct.setDireccion(txtDireccion.getText());
            ct.setTelefono(txtTelefono.getText());
            db.Insert(conn, ct);
            LimpiarCasillas();
        } catch (SQLException ex) {
        //Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_btnGuardarActionPerformed

    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
        try {
            
            db.Select(conn, ct, Integer.parseInt(txtId.getText()));
            txtNombre.setText(ct.getNombre());
            txtDireccion.setText(ct.getDireccion());
            txtTelefono.setText(ct.getTelefono());
        } catch (SQLException ex) {
        // Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_btnBuscarActionPerformed

    private void btnBorrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBorrarActionPerformed
        try {
            db.Delete(conn, ct, Integer.parseInt(txtId.getText()));
            LimpiarCasillas();
        } catch (SQLException ex) {
        // Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_btnBorrarActionPerformed

    private void btnEditarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditarActionPerformed
        try {
            ct.setId(Integer.parseInt(txtId.getText()));
            ct.setNombre(txtNombre.getText());
            ct.setDireccion(txtDireccion.getText());
            ct.setTelefono(txtTelefono.getText());
            db.Update(conn, ct, Integer.parseInt(txtId.getText()));
            LimpiarCasillas();
        } catch (SQLException ex) {
        // Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_btnEditarActionPerformed

